 // Transforming from Source to Target

@UiHandler("btnSource")

void onSource(ClickEvent e) {

 MaterialPathAnimator.animate(btnSource.getElement(), overlayTarget.getElement());

}

 // Transforming from Target to Source using reverseAnimate() method.

@UiHandler("btnClose")

void onClose(ClickEvent e) {

 MaterialPathAnimator.reverseAnimate(btnSource.getElement(), overlayTarget.getElement());

}

 MaterialPathAnimator animator = new MaterialPathAnimator(); 

animator.setSourceElement(btnOptionSource.getElement()); 

animator.setTargetElement(panelWithOptions.getElement()); 

animator.setDuration(txtDuration.getValue()); 

animator.setTargetShowDuration(txtTargetShowDuration.getValue()); 

animator.setExtraTransitionDuration(txtExtraTransitionDuration.getValue()); 

// To access the bridge element and stylize it. 

animator.setShadow(5); 

animator.setBackgroundColor(Color.RED); 

animator.setStyleProperty("color", "red"); 

// Call to perform path animation 

animator.animate();
